    private System.Timers.Timer timer;

    private int min = 25;
    private int sec = 00;
    private int count = 0;

    public void Count(object source, System.Timers.ElapsedEventArgs e)
    {
        if (sec > 0)
        {
            sec -= 1;
            if (sec == 0 && min % 5 == 0)
            {
                count += 1;
            }
        }
        else if (sec == 0 && min > 0)
        {
            min -= 1;
            sec = 59;
            if (min == 0)
            {
                sec = 0;
                timer.Stop();
                timer.Enabled = false;
            }
        }
        InvokeAsync(StateHasChanged);
    }

    public void Start()
    {
        if (timer != null)
        {
            timer.Dispose();
        }
        timer = new System.Timers.Timer(1000);
        timer.Elapsed += Count;
        timer.Enabled = true;
        timer.AutoReset = true;
    }

    public void Stop()
    {
        timer.Stop();
        timer.Enabled = false;
    }

    public void Reset()
    {
        sec = 00;
        min = 25;
        count = 0;
        timer.Stop();
    }

